package com.ericsson.jcat.jcatwebapp.config;import org.springframework.beans.factory.config.PropertyPlaceholderConfigurer;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.ComponentScan;import org.springframework.context.annotation.ComponentScan.Filter;import org.springframework.context.annotation.Configuration;import org.springframework.core.io.ClassPathResource;import org.springframework.stereotype.Controller;import com.ericsson.jcat.jcatwebapp.Application;@Configuration@ComponentScan(basePackageClasses = { Application.class }, excludeFilters = @Filter({ Controller.class,		Configuration.class }))class ApplicationConfig {	@Bean	public static PropertyPlaceholderConfigurer propertyPlaceholderConfigurer() {		PropertyPlaceholderConfigurer ppc = new PropertyPlaceholderConfigurer();		ppc.setBeanName("ppcc");		ppc.setLocations(new ClassPathResource[] { new ClassPathResource("/persistence.properties"), });		ppc.setIgnoreUnresolvablePlaceholders(true);		return ppc;	}}